CREATE OR REPLACE FUNCTION sip_obter_estrutura_proced ( cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/*
 IE_TIPO_P
	C - Código
	D - Descrição
	S - Sequência
*/
nr_seq_estrutura_w	bigint	:= 0;
nr_seq_estrutura_ww	bigint	:= 0;
ie_estrutura_w		varchar(1);
cd_area_w		bigint;
cd_especialidade_w	bigint;
cd_grupo_w		bigint;
cd_estrutura_w		varchar(40)	:= '';
ds_estrutura_w		varchar(80)	:= '';
ie_origem_proced_w	bigint;
ds_retorno_w		varchar(80);

C01 CURSOR FOR
	SELECT	nr_seq_estrutura,
		ie_estrutura
	from	sip_procedimento
	where	coalesce(cd_procedimento,cd_procedimento_p)		= cd_procedimento_p
	and	coalesce(ie_origem_proced,ie_origem_proced_w) 	= ie_origem_proced_w
	and	coalesce(cd_grupo_proc,cd_grupo_w)			= cd_grupo_w
	and	coalesce(cd_especialidade, cd_especialidade_w)	= cd_especialidade_w
	and	coalesce(cd_area_procedimento, cd_area_w) 		= cd_area_w
	order by
		coalesce(cd_procedimento,0),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_area_procedimento,0);


BEGIN

SELECT * FROM pls_obter_estrut_proc(cd_procedimento_p, ie_origem_proced_p, cd_area_w, cd_especialidade_w, cd_grupo_w, ie_origem_proced_w) INTO STRICT cd_area_w, cd_especialidade_w, cd_grupo_w, ie_origem_proced_w;

open C01;
loop
fetch C01 into
	nr_seq_estrutura_w,
	ie_estrutura_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_estrutura_w	= 'S') then
		nr_seq_estrutura_ww	:= nr_seq_estrutura_w;
	end if;
	end;
end loop;
close C01;

begin
select	coalesce(cd_estrutura,''),
	coalesce(ds_estrutura,'')
into STRICT	cd_estrutura_w,
	ds_estrutura_w
from	sip_estrutura_proc
where	nr_sequencia	= nr_seq_estrutura_ww;
exception
	when others then
	cd_estrutura_w	:= '';
	ds_estrutura_w	:= '';
end;

if (ie_tipo_p	= 'C') then
	ds_retorno_w	:= cd_estrutura_w;
elsif (ie_tipo_p	= 'D') then
	ds_retorno_w	:= ds_estrutura_w;
else
	ds_retorno_w	:= nr_seq_estrutura_ww;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
