CREATE OR REPLACE FUNCTION sip_obter_se_proc_regra ( nr_seq_regra_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_area_procedimento_p bigint, cd_especialidade_w bigint, cd_grupo_proc_w bigint) RETURNS varchar AS $body$
DECLARE


ie_estrutura_w		varchar(1)	:= 'S';

C01 CURSOR FOR
	SELECT	ie_estrutura
	from	sip_procedimento
	where	nr_seq_estrutura	= nr_seq_regra_p
	and	coalesce(cd_procedimento,cd_procedimento_p) 	= cd_procedimento_p
	and	((coalesce(cd_procedimento::text, '') = '') or (coalesce(ie_origem_proced,ie_origem_proced_p)= ie_origem_proced_p))
	and	coalesce(cd_area_procedimento,cd_area_procedimento_p)	= cd_area_procedimento_p
	and	coalesce(cd_especialidade,cd_especialidade_w)		= cd_especialidade_w
	and	coalesce(cd_grupo_proc,cd_grupo_proc_w)			= cd_grupo_proc_w
	order by
		coalesce(cd_procedimento,0),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_area_procedimento,0);


BEGIN

open C01;
loop
fetch C01 into
	ie_estrutura_W;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_estrutura_w	:= ie_estrutura_w;
	end;
end loop;
close C01;

return	ie_estrutura_w;

end;
$body$
