CREATE OR REPLACE FUNCTION siscolo_habilitar_atipias (nr_seq_siscolo_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15) := 'N';
qt_avaliacao_w		bigint;
qt_diagnostico_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_avaliacao_w
from	siscolo_cito_avaliacao
where	nr_seq_siscolo	= nr_seq_siscolo_p;

if (coalesce(qt_avaliacao_w,0) > 0) then
	begin

	select 	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	siscolo_cito_avaliacao
	where	ie_adequabilidade	= 'S'
	and	nr_seq_siscolo	= nr_seq_siscolo_p;

	select	count(*)
	into STRICT	qt_diagnostico_w
	from	siscolo_cito_diagnostico
	where	nr_seq_siscolo	= nr_seq_siscolo_p;

	if (coalesce(qt_diagnostico_w,0) > 0) then
		begin

		select 	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	siscolo_cito_diagnostico
		where	ie_normalidade_mat	= 'N'
		and	nr_seq_siscolo	= nr_seq_siscolo_p;

		end;
	end if;

	end;
else
	begin

	select	count(*)
	into STRICT	qt_diagnostico_w
	from	siscolo_cito_diagnostico
	where	nr_seq_siscolo		= nr_seq_siscolo_p;

	if (coalesce(qt_diagnostico_w,0) > 0) then
		begin

		select 	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	siscolo_cito_diagnostico
		where	ie_normalidade_mat	= 'N'
		and	nr_seq_siscolo	= nr_seq_siscolo_p;

		end;
	else
		ds_retorno_w := 'S';
	end if;

	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
