CREATE OR REPLACE FUNCTION sismama_consiste_mastectomia ( nr_seq_sismama_p bigint, ie_lado_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15) := 'N';
ie_consiste_implante_w	varchar(10) := 'N';


BEGIN

ie_consiste_implante_w := obter_param_usuario(1125, 134, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_consiste_implante_w);

if (ie_lado_p = 'D') then

	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	sismama_anamnese_rad a
	where	a.nr_seq_sismama = nr_seq_sismama_p
	and	(((a.dt_mastectomia_dir IS NOT NULL AND a.dt_mastectomia_dir::text <> '') and coalesce(a.dt_plastic_implante_dir::text, '') = '') or ((a.dt_mastectomia_dir IS NOT NULL AND a.dt_mastectomia_dir::text <> '') and (a.dt_plastic_implante_dir IS NOT NULL AND a.dt_plastic_implante_dir::text <> '') and coalesce(ie_consiste_implante_w,'N') = 'N'));


elsif (ie_lado_p = 'E') then

	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	sismama_anamnese_rad a
	where	a.nr_seq_sismama = nr_seq_sismama_p
	and	(((a.dt_mastectomia_esq IS NOT NULL AND a.dt_mastectomia_esq::text <> '') and coalesce(a.dt_plastic_implante_esq::text, '') = '') or ((a.dt_mastectomia_esq IS NOT NULL AND a.dt_mastectomia_esq::text <> '') and (a.dt_plastic_implante_esq IS NOT NULL AND a.dt_plastic_implante_esq::text <> '') and coalesce(ie_consiste_implante_w,'N') = 'N'));

end if;

return	coalesce(ds_retorno_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
