CREATE OR REPLACE FUNCTION sismama_obter_se_anam_anterior (nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS varchar AS $body$
DECLARE

 
cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;
ds_retorno_w			varchar(15) := 'N';
					

BEGIN 
 
begin 
select	cd_pessoa_fisica 
into STRICT	cd_pessoa_fisica_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p  LIMIT 1;
exception 
when others then 
	cd_pessoa_fisica_w := '0';
end;
 
begin 
select	coalesce('S','N') 
into STRICT	ds_retorno_w 
from	sismama_atendimento a, 
	atendimento_paciente b 
where	a.nr_atendimento = b.nr_atendimento 
and	b.cd_pessoa_fisica = cd_pessoa_fisica_w 
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
and	exists (SELECT	1 
		from	sismama_cit_anamnese x 
		where	x.nr_seq_sismama = a.nr_sequencia  LIMIT 1)  LIMIT 1;
exception 
when others then 
	ds_retorno_w := 'N';
end;
 
return	ds_retorno_w;
 
end;
$body$
