CREATE OR REPLACE FUNCTION sis_obter_reg_integridade ( nm_tabela_p text, nm_tabela_referencia_p text) RETURNS bigint AS $body$
DECLARE


nm_atributo_w			varchar(50);
qt_registro_w			bigint	:= 0;
qt_maior_w			bigint	:= 0;
c01 CURSOR FOR
SELECT	b.nm_atributo
from	integridade_atributo b,
	integridade_referencial a
where	a.nm_integridade_referencial	= b.nm_integridade_referencial
and	a.nm_tabela		= nm_tabela_p
and	a.nm_tabela_referencia	= nm_tabela_referencia_p;


BEGIN

open C01;
loop
fetch C01 into
	nm_atributo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	qt_registro_w := obter_valor_dinamico('select count(*) from ' || nm_tabela_p || ' where ' || nm_atributo_w || ' is not null', qt_registro_w);
	if (qt_registro_w > qt_maior_w) then
		qt_maior_w	:= qt_registro_w;
	end if;
	end;
end loop;
close C01;


return	coalesce(qt_maior_w,0);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
