CREATE OR REPLACE FUNCTION sis_obter_se_cd_barras_existe ( nm_usuario_p text, cd_barras_w text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
FROM usuario a
LEFT OUTER JOIN pessoa_fisica b ON (a.cd_pessoa_fisica = b.cd_pessoa_fisica)
WHERE a.nm_usuario <> nm_usuario_p and ((a.cd_barras = cd_barras_w) or (b.cd_barras_pessoa = cd_barras_w));

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
