CREATE OR REPLACE FUNCTION sl_obter_dados_serv_prof_dia (cd_profissional_p text, dt_referencia_p timestamp, nr_seq_unidade_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_sl_w		bigint;
dt_fim_w		timestamp;
dt_inicio_w		timestamp;
ds_retorno_w		varchar(90);
nr_seq_interno_w	bigint;
cd_servico_w		integer := 0;
qt_minutos_w		integer := 0;

C01 CURSOR FOR
	SELECT	dt_fim,
		dt_inicio
	from	sl_unid_atend a,
		unidade_atendimento b
	where	b.nr_seq_interno = a.nr_seq_unidade
	and	coalesce(cd_executor_fim_serv,cd_executor_inic_serv) = cd_profissional_p
	and	trunc(dt_prevista) = trunc(dt_referencia_p);



BEGIN

open C01;
loop
fetch C01 into
	dt_fim_w,
	dt_inicio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	cd_servico_w	:= cd_servico_w + 1;

	if (dt_fim_w IS NOT NULL AND dt_fim_w::text <> '') then
		qt_minutos_w	:= qt_minutos_w + trunc((dt_fim_w - dt_inicio_w)*1440);
	end if;

	end;
end loop;
close C01;


if (ie_opcao_p = 'TT') then
	ds_retorno_w	:= qt_minutos_w;
elsif (ie_opcao_p = 'TS') then
	ds_retorno_w	:= cd_servico_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
