SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sl_obter_ult_serv_prof (cd_profissional_p text, dt_referencia_p timestamp, nr_seq_unidade_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_sl_w		bigint;
dt_fim_w		timestamp;
dt_inicio_w		timestamp;
cd_unidade_basica_w	varchar(10);
cd_unidade_compl_w	varchar(10);
cd_setor_atendimento_w	integer;
ds_retorno_w		varchar(90);
nr_seq_interno_w	bigint;


BEGIN

select 	max(nr_sequencia)
into STRICT	nr_seq_sl_w
from	sl_unid_atend
where	cd_executor = cd_profissional_p
and	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_prevista) = ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_referencia_p);

select	max(dt_fim),
	max(dt_inicio),
	max(cd_unidade_basica),
	max(cd_unidade_compl),
	max(cd_setor_atendimento),
	max(a.nr_seq_unidade)
into STRICT	dt_fim_w,
	dt_inicio_w,
	cd_unidade_basica_w,
	cd_unidade_compl_w,
	cd_setor_atendimento_w,
	nr_seq_interno_w
from	sl_unid_atend a,
	unidade_atendimento b
where	b.nr_seq_interno = a.nr_seq_unidade
and	a.nr_sequencia = nr_seq_sl_w;


if (ie_opcao_p = 'DF') then
	ds_retorno_w	:= to_char(dt_fim_w, 'hh24:mi');
elsif (ie_opcao_p = 'DI') then
	ds_retorno_w	:= to_char(dt_inicio_w, 'hh24:mi');
elsif (ie_opcao_p = 'UN') then
	ds_retorno_w	:= cd_unidade_basica_w || cd_unidade_compl_w;
elsif (ie_opcao_p = 'SUN') then
	ds_retorno_w	:= nr_seq_interno_w;
elsif (ie_opcao_p = 'SEQ') then
	ds_retorno_w	:= nr_seq_sl_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
