CREATE OR REPLACE FUNCTION smart_obter_desc_servico_dieta (nr_prescricao_p prescr_medica.nr_prescricao%type, dt_horario_p timestamp) RETURNS varchar AS $body$
DECLARE

ds_servico_w            nut_servico.ds_servico%type;
cd_estabelecimento_w    prescr_medica.cd_estabelecimento%type;
cd_setor_atendimento_w  prescr_medica.cd_setor_atendimento%type;

BEGIN
if (dt_horario_p IS NOT NULL AND dt_horario_p::text <> '' AND nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
        select  max(a.cd_estabelecimento),
                coalesce(max(a.cd_setor_atendimento),0)
        into STRICT    cd_estabelecimento_w,
                cd_setor_atendimento_w
        from    prescr_medica a
        where   a.nr_prescricao = nr_prescricao_p;

        select  max(b.ds_servico)
        into STRICT    ds_servico_w
        from    nut_servico_horario a,
                nut_servico b where     coalesce(a.cd_setor_atendimento, cd_setor_atendimento_w) = cd_setor_atendimento_w
        and (coalesce(b.cd_estabelecimento, cd_estabelecimento_w, 0) = coalesce(cd_estabelecimento_w,0))
        and     a.nr_seq_servico = b.nr_sequencia
        and     b.ie_tipo_prescricao_servico  = 1
        and     coalesce(b.ie_situacao,'A') = 'A'
        and     'S' = obter_se_hora_entre(dt_horario_p,
                        coalesce(a.ds_horarios_html,to_date(a.ds_horarios,'hh24:mi')),
                        coalesce(a.ds_horarios_fim_html,to_date(a.ds_horarios_fim,'hh24:mi'))) LIMIT 1;
end if;
return ds_servico_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
