SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION smart_obter_se_sae ( nr_seq_sae_proced_p bigint, cd_estabelecimento_p bigint, ie_evento_p text, ie_opcao_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_existe_w				varchar(3) := 'N';
ie_integra_w			varchar(3) := 'N';



BEGIN

if ( ie_opcao_p = 'PE' ) then

	Select 	coalesce(max('S'),'N')
	into STRICT	ie_existe_w
	from   	regra_conv_meio_ext a,
			conversao_meio_externo b
	where  	b.nr_seq_regra = a.nr_sequencia
	and    	coalesce(a.IE_SITUACAO,'A') = 'A'
	and    	b.ie_sistema_externo = 'SMART'
	and    	upper(b.nm_tabela) = 'PE_PROCEDIMENTO'
	and    	b.cd_interno = nr_seq_sae_proced_p;
	
		
elsif ( ie_opcao_p = 'RC' ) then

	Select 	coalesce(max('S'),'N')
	into STRICT	ie_existe_w
	from   	pe_procedimento c
	where  	c.nr_sequencia = nr_seq_sae_proced_p
	and		coalesce(c.IE_SITUACAO,'A') = 'A'
	and		coalesce(ie_sistema_terceiro,'N') = 'S';


end if;

if ( ie_existe_w = 'S' ) then
	
		Select 	coalesce(max('S'),'N')
		into STRICT	ie_integra_w
		from   	INTPD_EVENTOS a,
				INTPD_EVENTOS_SISTEMA b,
				INTPD_EVENTOS_SISTEMA_REST c
		where  	b.nr_seq_evento = a.nr_sequencia
		and    	c.nr_seq_evento_sistema = b.nr_sequencia
		and    	a.IE_EVENTO = ie_evento_p
		and    	coalesce(a.IE_SITUACAO,'A') = 'A'
		and    	coalesce(b.IE_SITUACAO,'A') = 'A'
		and    	coalesce(c.IE_INTEGRAR,'S') = 'S'
		and (coalesce(c.cd_estabelecimento::text, '') = '' or (c.cd_estabelecimento = coalesce(cd_estabelecimento_p,c.cd_estabelecimento)));
		
	
end if;

return	ie_integra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION smart_obter_se_sae ( nr_seq_sae_proced_p bigint, cd_estabelecimento_p bigint, ie_evento_p text, ie_opcao_p text, nm_usuario_p text) FROM PUBLIC;

