SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION snd_obter_alergias (cd_pessoa_fisica_p text,ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* ie_opcao_p -> eventuais futuras customizações */
		  
 
ds_alergia_w varchar(80);
ds_retorno_w varchar(2000) := '';
ds_string_retorno_w varchar(2000) := '';

C01 CURSOR FOR 
  SELECT substr(obter_descricao_padrao('TIPO_ALERGIA','DS_TIPO_ALERGIA',a.nr_seq_tipo),1,254) 
	from  paciente_alergia a 
	where	(a.nr_seq_tipo IS NOT NULL AND a.nr_seq_tipo::text <> '') 
	and		a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and (SELECT 	w.ie_alimento 
			 from  tipo_alergia w 
			 where  a.nr_seq_tipo = w.nr_sequencia 
			 ) = 'S';


BEGIN 
	open c01;
		loop 
			fetch c01 into 
			ds_alergia_w;
		  EXIT WHEN NOT FOUND; /* apply on c01 */
				if length(ds_alergia_w) > 0 then 
				ds_retorno_w := ds_retorno_w || ds_alergia_w || ', ';
				end if;
		end loop;
	close C01;
	 
	if length(trim(both substr((ds_retorno_w),1,length(ds_retorno_w)-2) || '.' )) > 1 then 
		ds_string_retorno_w := trim(both substr((ds_retorno_w),1,length(ds_retorno_w)-2) || '.' );
	else 
		ds_string_retorno_w := null;
	end if;
return ds_string_retorno_w;
 
--tratamento em pontuaçao final da string e usar separador ' , ' apenas se houver informaçao. 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
