CREATE OR REPLACE FUNCTION snd_obter_habitos_observacao (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p -> eventuais futuras customizações */

ds_observacao_w  varchar(255);
ds_retorno_w varchar(2000) := '';
ds_string_retorno_w varchar(2000) := '';

C01 CURSOR FOR
	SELECT 	ds_observacao
	into STRICT	ds_retorno_w
	from   	paciente_habito_vicio a
	where  	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(a.dt_fim::text, '') = ''
	and    	upper(a.ie_classificacao) = upper('C');


BEGIN
	open c01;
		loop
			fetch c01 into
			ds_observacao_w;
		    EXIT WHEN NOT FOUND; /* apply on c01 */
				if length(ds_observacao_w) > 0 then
				ds_retorno_w := ds_retorno_w || ds_observacao_w || ', ';
				end if;
		end loop;
	close C01;

	if length(trim(both substr((ds_retorno_w),1,length(ds_retorno_w)-2) || '.' )) > 1 then
		ds_string_retorno_w := trim(both substr((ds_retorno_w),1,length(ds_retorno_w)-2) || '.' );
	else
		ds_string_retorno_w := null;
	end if;
return ds_string_retorno_w;

--tratamento em pontuaçao final da string e usar separador ' , ' apenas se houver informaçao.
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
