SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION snomed_concept_info (ds_id_p text) RETURNS varchar AS $body$
DECLARE


ds_concept_info_w	varchar(4000);

c01 CURSOR FOR
SELECT distinct a.ds_languagecode, a.ds_term
from snomed_descricao a,
  (select distinct ds_conceptid from snomed_descricao where ds_id = ds_id_p) b
where a.ds_conceptid = b.ds_conceptid
and a.ie_situacao = '1'
and a.ds_effectivetime = (select max(ds_effectivetime) from snomed_descricao where ds_id = a.ds_id)
order by a.ds_term
;

BEGIN

begin 
	select ds_term || chr(10) || 'SCTID: ' || ds_conceptid || chr(10)
	into STRICT ds_concept_info_w
	from (
	  SELECT a.ds_conceptid, a.ds_term
	  from snomed_descricao a, 
		(SELECT distinct ds_conceptid from snomed_descricao where ds_id = ds_id_p) b
	  where a.ds_conceptid = b.ds_conceptid
	  and a.ie_situacao = '1'
	  and a.ds_typeid = '900000000000003001'
	  order by ds_effectivetime desc
	) alias3 LIMIT 1;
exception when others then
	ds_concept_info_w := '';
end;

if (ds_concept_info_w IS NOT NULL AND ds_concept_info_w::text <> '') then
	for c01_row in c01 loop
		ds_concept_info_w := ds_concept_info_w || chr(10) || c01_row.ds_languagecode || ' ' || c01_row.ds_term;
	end loop;
end if;

return ds_concept_info_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
