CREATE OR REPLACE FUNCTION somar_atrib_titulo_receber_liq (nr_titulo_p bigint, nm_atributo_p text, dt_baixa_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;

dt_final_w		timestamp;


BEGIN

dt_final_w	:= fim_dia(dt_baixa_p);

if (upper(nm_atributo_p) = 'VL_GLOSA') then
	select	sum(vl_glosa)
	into STRICT	vl_retorno_w
	from 	titulo_receber_liq
	where	nr_titulo	= nr_titulo_p
	and	coalesce(ie_lib_caixa, 'S') = 'S'
	and (coalesce(dt_baixa_p::text, '') = '' or dt_recebimento <= dt_final_w);

elsif (upper(nm_atributo_p) = 'VL_RECEBIDO') then
	select	coalesce(sum(vl_recebido),0)
	into STRICT	vl_retorno_w
	from 	titulo_receber_liq
	where	nr_titulo	= nr_titulo_p
	and	coalesce(ie_lib_caixa, 'S') = 'S'
	and (coalesce(dt_baixa_p::text, '') = '' or dt_recebimento <= dt_final_w);

elsif (upper(nm_atributo_p) = 'VL_DESCONTOS') then
	select	coalesce(sum(vl_descontos),0)
	into STRICT	vl_retorno_w
	from 	titulo_receber_liq
	where	nr_titulo	= nr_titulo_p
	and	coalesce(ie_lib_caixa, 'S') = 'S'
	and (coalesce(dt_baixa_p::text, '') = '' or dt_recebimento <= dt_final_w);

elsif (upper(nm_atributo_p) = 'VL_JUROS') then
	select	sum(vl_juros)
	into STRICT	vl_retorno_w
	from 	titulo_receber_liq
	where	nr_titulo	= nr_titulo_p
	and	coalesce(ie_lib_caixa, 'S') = 'S'
	and (coalesce(dt_baixa_p::text, '') = '' or dt_recebimento <= dt_final_w);

elsif (upper(nm_atributo_p) = 'VL_MULTA') then
	select	sum(vl_multa)
	into STRICT	vl_retorno_w
	from 	titulo_receber_liq
	where	nr_titulo	= nr_titulo_p
	and	coalesce(ie_lib_caixa, 'S') = 'S'
	and (coalesce(dt_baixa_p::text, '') = '' or dt_recebimento <= dt_final_w);

elsif (upper(nm_atributo_p) = 'VL_REC_MAIOR') then
	select	sum(vl_rec_maior)
	into STRICT	vl_retorno_w
	from 	titulo_receber_liq
	where	nr_titulo	= nr_titulo_p
	and	coalesce(ie_lib_caixa, 'S') = 'S'
	and (coalesce(dt_baixa_p::text, '') = '' or dt_recebimento <= dt_final_w);

elsif (upper(nm_atributo_p) = 'VL_CANCELADO') then
	select	sum(vl_recebido)
	into STRICT	vl_retorno_w
	from 	titulo_receber_liq
	where	nr_titulo	= nr_titulo_p
	and	coalesce(ie_lib_caixa, 'S') = 'S'
	and (coalesce(dt_baixa_p::text, '') = '' or dt_recebimento <= dt_final_w)
	and	cd_tipo_recebimento in (SELECT	cd_tipo_recebimento
					from	tipo_recebimento
					where	ie_tipo_consistencia = 4);

elsif (upper(nm_atributo_p) = 'VL_PERDAS') then
	select	sum(vl_perdas)
	into STRICT	vl_retorno_w
	from 	titulo_receber_liq
	where	nr_titulo	= nr_titulo_p
	and	coalesce(ie_lib_caixa, 'S') = 'S'
	and (coalesce(dt_baixa_p::text, '') = '' or dt_recebimento <= dt_final_w);

elsif (upper(nm_atributo_p)	= 'VL_NOTA_CREDITO') then
	select	sum(a.vl_nota_credito)
	into STRICT	vl_retorno_w
	from	titulo_receber_liq a
	where	a.nr_titulo	= nr_titulo_p
	and (coalesce(dt_baixa_p::text, '') = '' or dt_recebimento <= dt_final_w);

elsif (upper(nm_atributo_p)	= 'VL_CAMBIAL_ATIVO') then
	select	sum(a.vl_cambial_ativo)
	into STRICT	vl_retorno_w
	from	titulo_receber_liq a
	where	a.nr_titulo	= nr_titulo_p
	and (coalesce(dt_baixa_p::text, '') = '' or dt_recebimento <= dt_final_w);

elsif (upper(nm_atributo_p)	= 'VL_CAMBIAL_PASSIVO') then
	select	sum(a.vl_cambial_passivo)
	into STRICT	vl_retorno_w
	from	titulo_receber_liq a
	where	a.nr_titulo	= nr_titulo_p
	and (coalesce(dt_baixa_p::text, '') = '' or dt_recebimento <= dt_final_w);

elsif (upper(nm_atributo_p)    = 'VL_RECURSO') then
    select  sum(a.vl_recurso)
    into STRICT    vl_retorno_w
    from    titulo_receber_liq a
    where   a.nr_titulo    = nr_titulo_p
    and (coalesce(dt_baixa_p::text, '') = '' or dt_recebimento <= dt_final_w);

end if;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
