CREATE OR REPLACE FUNCTION somar_hora_real ( dt_inicio_real_p timestamp, dt_fim_real_p timestamp, cd_executor_p text, cd_cnpj_p text, nr_seq_etapa_p bigint) RETURNS bigint AS $body$
DECLARE


qt_hora_real_w	real;


BEGIN

select	coalesce(sum(round((dividir(coalesce(e.qt_min_ativ,0), 60))::numeric,2)),0)
into STRICT	qt_hora_real_w
from	proj_rat d,
	proj_rat_ativ e,
	proj_cron_etapa a,
	com_cliente c,
	proj_cronograma b
where	a.dt_inicio_prev between dt_inicio_real_p and dt_fim_real_p
and (c.cd_cnpj		= cd_cnpj_p)
and	b.nr_sequencia		= a.nr_seq_cronograma
and	b.nr_seq_cliente		= c.nr_sequencia
and	e.nr_seq_etapa_cron	= a.nr_sequencia
and	((d.cd_executor		= cd_executor_p) or (cd_executor_p = '0'))
and	e.nr_seq_rat		= d.nr_sequencia
and	a.nr_seq_etapa		= nr_seq_etapa_p
and	coalesce(a.ie_situacao,'A') = 'A';

return	qt_hora_real_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
