CREATE OR REPLACE FUNCTION somente_letra ( ds_campo_p text) RETURNS varchar AS $body$
DECLARE


ds_campo_w    	varchar(255);
i     			integer;
k			integer;
ds_letra_w		varchar(26);


BEGIN
ds_letra_w		:= 'abcdefghijklmnopqrstuvwxyz';
ds_campo_w		:= '';
if (coalesce(ds_campo_p::text, '') = '') or (ds_campo_p = '') then
	ds_campo_w	:= '';
else
	begin
	FOR i IN 1..length(ds_campo_p) LOOP
 		begin
		k	:= instr(ds_letra_w, lower(substr(ds_campo_p, i, 1)) , -1);
	 	if (k <> 0)  then
  			ds_campo_w := ds_campo_w || substr(ds_campo_p, i, 1);
		end if;
		end;
	END LOOP;
	end;
end if;

RETURN ds_Campo_w;
END;
