CREATE OR REPLACE FUNCTION somente_nao_numero (vl_valor_p text) RETURNS varchar AS $body$
DECLARE


vl_nao_numero_w	varchar(255) := null;
i     		integer;


BEGIN
if (vl_valor_p IS NOT NULL AND vl_valor_p::text <> '') then
	begin
	for i in 1..length(vl_valor_p) loop
		begin
	 	if (substr(vl_valor_p, i, 1) not in ('1','2','3','4','5','6','7','8','9','0')) then
			begin
			if (coalesce(vl_nao_numero_w::text, '') = '') or (coalesce(length(vl_nao_numero_w),0) < 255) then
				begin
				vl_nao_numero_w := vl_nao_numero_w || substr(vl_valor_p, i, 1);
				end;
			end if;
			end;
		end if;
		end;
	end loop;
	end;
end if;
return vl_nao_numero_w;
end;
$body$
