CREATE OR REPLACE FUNCTION somente_numero_char ( ds_campo_p text) RETURNS varchar AS $body$
DECLARE


ds_campo_w    	varchar(255);
i	     		integer;


BEGIN

ds_campo_w		:= '';
if	not(coalesce(ds_campo_p::text, '') = '') then
	begin

	for i in 1..length(ds_campo_p) loop
 		begin
	 	if (substr(ds_campo_p, i, 1) in ('1','2','3','4','5','6','7','8','9','0')) then
  			ds_campo_w := ds_campo_w || substr(ds_campo_p, i, 1);
		end if;
		end;
	end loop;

	if (coalesce(ds_campo_w::text, '') = '') then
		ds_campo_w := '';
	end if;

	end;
end if;

return ds_campo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
