SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION spa_obter_dados_adiantamento ( nr_adiantamento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*IE_OPCAO_P 
V - Valor do adiantamento 
S - Saldo do adiantamento 
*/
				 
 
vl_adiantamento_w 	adiantamento.vl_adiantamento%type;
vl_saldo_w		adiantamento.vl_saldo%type;


BEGIN 
 
if (coalesce(nr_adiantamento_p,0) = 0) then 
	return '';
end if;
 
SELECT sum(coalesce(a.vl_adiantamento,0)), 
	sum(coalesce(a.vl_saldo,0)) 
into STRICT 	vl_adiantamento_w, 
	vl_saldo_w 
FROM 	adiantamento_v 	a 
WHERE 	a.nr_adiantamento 	= nr_adiantamento_p;
 
if (ie_opcao_p = 'S') then 
	return vl_saldo_w;
elsif (ie_opcao_p = 'V') then 
	return vl_adiantamento_w;
end if;
 
return	'';
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
