CREATE OR REPLACE FUNCTION spa_obter_dados_aprov (nr_seq_spa_p bigint, nm_usuario_aprov_p text, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE

dt_aprovacao_w	timestamp;

BEGIN

if (nr_seq_spa_p IS NOT NULL AND nr_seq_spa_p::text <> '') and (nm_usuario_aprov_p IS NOT NULL AND nm_usuario_aprov_p::text <> '') then

	select 	max(k.dt_aprovacao)
	into STRICT	dt_aprovacao_w
	from	spa_aprovacao k
	where	k.nr_seq_spa 		= nr_seq_spa_p
	and	k.nm_usuario_aprov	= nm_usuario_aprov_p
	and	(k.dt_lib_aprov IS NOT NULL AND k.dt_lib_aprov::text <> '')
	and	(k.dt_aprovacao IS NOT NULL AND k.dt_aprovacao::text <> '');

end if;

return	dt_aprovacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
