CREATE OR REPLACE FUNCTION spa_obter_dados_negociacao ( nr_seq_spa_p bigint, cd_forma_negociacao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


vl_negociado_w		spa_negociacao.vl_negociado%type := 0;
ds_retorno_w		varchar(200) := '';

BEGIN

if (coalesce(nr_seq_spa_p,0) > 0) then
	if ( ie_opcao_p = 'V') then
		select 	sum(coalesce(vl_negociado,0))
		into STRICT   	vl_negociado_w
		from   	spa_negociacao
		where  	cd_forma_negociacao 	= cd_forma_negociacao_p
		and  	nr_seq_spa 		= nr_seq_spa_p;

		ds_retorno_w :=  to_char(vl_negociado_w);
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
