SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION spa_obter_dados_perda_cta_rec ( nr_seq_perda_cta_rec_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*ie_opcao_p 
	DP - Data da perda 
	NT - Numero do titulo 
	NPF - Pessoa fisica 
	DMP - Descrição Motivo perda 
	VS - Valor do saldo	 
*/
 
dt_perda_w			PERDA_CONTAS_RECEBER.dt_perda%type;
nr_titulo_w			PERDA_CONTAS_RECEBER.nr_titulo%type;
cd_pessoa_fisica_w		PERDA_CONTAS_RECEBER.cd_pessoa_fisica%type;
nr_seq_motivo_perda_w		PERDA_CONTAS_RECEBER.nr_seq_motivo_perda%type;
vl_saldo_w			PERDA_CONTAS_RECEBER.vl_saldo%type;
ds_motivo_perda_w		varchar(255);
ds_pessoa_fisica_w		varchar(60);

BEGIN
if (coalesce(nr_seq_perda_cta_rec_p,0) = 0) then 
	return '';
end if;
begin 
	select dt_perda, 
		nr_titulo, 
		cd_pessoa_fisica, 
		nr_seq_motivo_perda, 
		coalesce(vl_saldo,0) vl_saldo 
	into STRICT 	dt_perda_w, 
		nr_titulo_w, 
		cd_pessoa_fisica_w, 
		nr_seq_motivo_perda_w, 
		vl_saldo_w 
	from  PERDA_CONTAS_RECEBER 
	where  nr_sequencia = nr_seq_perda_cta_rec_p;
exception 
when others then 
	return '';
end;
if (ie_opcao_p = 'DP') then 
	return dt_perda_w;
elsif (ie_opcao_p = 'NT') then 
	return nr_titulo_w;
elsif (ie_opcao_p = 'NPF') then 
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
		select 	substr(obter_nome_pf(cd_pessoa_fisica_w),1,60) 
		into STRICT	ds_pessoa_fisica_w 
		;
		return 	ds_pessoa_fisica_w;
	end if;	
elsif (ie_opcao_p = 'DMP')then 
	if (coalesce(nr_seq_motivo_perda_w,0) > 0) then 
		select 	substr(OBTER_DESC_MOTIVO_PERDA_FIN(nr_seq_motivo_perda_w),1,255) 
		into STRICT 	ds_motivo_perda_w 
		;
		return 	ds_motivo_perda_w;
	end if;	
elsif (ie_opcao_p = 'VS') then 
	return vl_saldo_w;
end if;
return	'';
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION spa_obter_dados_perda_cta_rec ( nr_seq_perda_cta_rec_p bigint, ie_opcao_p text) FROM PUBLIC;

