CREATE OR REPLACE FUNCTION spa_obter_se_exige_item ( nr_seq_spa_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
qt_mov_conta_w		bigint;
nr_seq_tipo_w		bigint;
ie_exige_item_w		varchar(1);
qt_item_spa_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_mov_conta_w
from	spa_movimento
where	cd_classificacao = 2
and	nr_seq_spa = nr_seq_spa_p;

if (qt_mov_conta_w > 0) then

	select	max(nr_seq_tipo)
	into STRICT	nr_seq_tipo_w
	from	spa
	where	nr_sequencia = nr_seq_spa_p;

	select	coalesce(max(ie_exige_item),'N')
	into STRICT	ie_exige_item_w
	from	spa_classif_tipo
	where	nr_seq_tipo = nr_seq_tipo_w;

	if (ie_exige_item_w = 'S') then

		select	count(*)
		into STRICT	qt_item_spa_w
		from	spa_movimento a,
			spa_movimento_item b
		where	a.nr_sequencia = b.nr_seq_movimento
		and	a.cd_classificacao = 2
		and	a.nr_seq_spa = nr_seq_spa_p;

		if (qt_item_spa_w = 0) then
			ds_retorno_w := 'S';
		end if;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
