CREATE OR REPLACE FUNCTION spa_obter_se_motivo_lib_tipo ( nr_seq_motivo_p bigint, nr_seq_tipo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1)	:= 'S';
qt_regra_w	bigint	:= 0;


BEGIN

if (nr_seq_motivo_p IS NOT NULL AND nr_seq_motivo_p::text <> '') and (nr_seq_tipo_p IS NOT NULL AND nr_seq_tipo_p::text <> '') then

	select	count(*)
	into STRICT	qt_regra_w
	from	spa_motivo_tipo
	where	nr_seq_tipo = nr_seq_tipo_p;

	if (qt_regra_w > 0) then
		select	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	spa_motivo_tipo
		where	nr_seq_motivo = nr_seq_motivo_p
		and	nr_seq_tipo = nr_seq_tipo_p
		and	ie_situacao = 'A';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
