CREATE OR REPLACE FUNCTION spa_obter_se_movimento_pessoa ( nr_seq_spa_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

			 
cd_classificacao_w	integer;
nr_titulo_w		bigint;
nr_seq_nf_w		bigint;
nr_interno_conta_w	bigint;
nr_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);
ds_retorno_w		varchar(1);

C01 CURSOR FOR 
	SELECT	cd_classificacao, 
		nr_titulo, 
		nr_seq_nf, 
		nr_interno_conta, 
		nr_atendimento 
	from	spa_movimento 
	where	nr_seq_spa = nr_seq_spa_p;


BEGIN 
 
ds_retorno_w	:= 'N';
 
open C01;
loop 
fetch C01 into	 
	cd_classificacao_w, 
	nr_titulo_w, 
	nr_seq_nf_w, 
	nr_interno_conta_w, 
	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	cd_pessoa_fisica_w	:= null;
	 
	if (cd_classificacao_w = 1) then 
		 
		select	coalesce(max(cd_pessoa_fisica),'0') 
		into STRICT	cd_pessoa_fisica_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_w;
		 
		if (cd_pessoa_fisica_w = cd_pessoa_fisica_p) then 
			ds_retorno_w	:= 'S';
		end if;
		 
	elsif (cd_classificacao_w = 2) then 
		 
		select	coalesce(max(b.cd_pessoa_fisica),'0') 
		into STRICT	cd_pessoa_fisica_w 
		from	conta_paciente a, 
			atendimento_paciente b 
		where	a.nr_atendimento = b.nr_atendimento 
		and	a.nr_interno_conta = nr_interno_conta_w;
		 
		if (cd_pessoa_fisica_w = cd_pessoa_fisica_p) then 
			ds_retorno_w	:= 'S';
		end if;
		 
	elsif (cd_classificacao_w = 3) then 
		 
		select	coalesce(max(cd_pessoa_fisica),'0') 
		into STRICT	cd_pessoa_fisica_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_w;
		 
		if (cd_pessoa_fisica_w = cd_pessoa_fisica_p) then 
			ds_retorno_w	:= 'S';
		end if;
		 
	elsif (cd_classificacao_w = 4) then 
		 
		select	coalesce(max(cd_pessoa_fisica),'0') 
		into STRICT	cd_pessoa_fisica_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_w;
		 
		if (cd_pessoa_fisica_w = cd_pessoa_fisica_p) then 
			ds_retorno_w	:= 'S';
		end if;
		 
	end if;
	 
	end;
end loop;
close C01;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
