CREATE OR REPLACE FUNCTION spa_obter_valor_movimento_neg ( nr_seq_spa_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_retorno_w		double precision := 0;
vl_atual_w		double precision := 0;
cd_classificacao_w	integer;
nr_seq_movimento_w	bigint;
nr_seq_nf_w		bigint;
nr_titulo_w		bigint;
nr_seq_protocolo_w	bigint;

C01 CURSOR FOR 
	SELECT	cd_classificacao, 
		nr_sequencia, 
		nr_seq_nf, 
		nr_titulo, 
		nr_seq_protocolo 
	from	spa_movimento 
	where	nr_seq_spa = nr_seq_spa_p;
			

BEGIN 
 
if (coalesce(nr_seq_spa_p,0) > 0) then 
 
	open C01;
	loop 
	fetch C01 into 
		cd_classificacao_w, 
		nr_seq_movimento_w, 
		nr_seq_nf_w, 
		nr_titulo_w, 
		nr_seq_protocolo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		if (cd_classificacao_w = 2) then 
		 
			select	coalesce(sum(vl_negociado),0) 
			into STRICT	vl_atual_w 
			from	spa_movimento a, 
				spa_movimento_item b 
			where	a.nr_sequencia = b.nr_seq_movimento 
			and	a.nr_sequencia = nr_seq_movimento_w;
			 
			vl_retorno_w	:= vl_retorno_w + vl_atual_w;
 
		elsif (cd_classificacao_w = 3) then 
		 
			select	coalesce((obter_dados_nota_fiscal(nr_seq_nf_w, 6))::numeric ,0) 
			into STRICT	vl_atual_w 
			;
			 
			vl_retorno_w	:= vl_retorno_w + vl_atual_w;
		 
		elsif (cd_classificacao_w = 4) then 
		 
			select	coalesce((obter_dados_titulo_receber(nr_titulo_w, 'VS'))::numeric ,0) 
			into STRICT	vl_atual_w 
			;
			 
			vl_retorno_w	:= vl_retorno_w + vl_atual_w;
		 
		elsif (cd_classificacao_w = 5) then 
		 
			select	coalesce((substr(obter_total_protocolo(nr_seq_protocolo_w),1,40))::numeric ,0) 
			into STRICT	vl_atual_w 
			;
			 
			vl_retorno_w	:= vl_retorno_w + vl_atual_w;
		 
		end if;
		 
		end;
	end loop;
	close C01;
 
end if;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
