CREATE OR REPLACE FUNCTION sped_obter_campo_valor ( vl_campo_p bigint, qt_casas_decimais bigint default 2) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(30) := '';
vl_campo_w		varchar(30);
vl_novo_campo_w		varchar(30);
i			bigint  := 0;
casas_decimais 		varchar(10) := '.99';


BEGIN
	if (vl_campo_p IS NOT NULL AND vl_campo_p::text <> '') then
	begin
		vl_campo_w := to_char(vl_campo_p);

		if (qt_casas_decimais = 3) then
			casas_decimais := '.999';
		end if;
		
		select	replace(replace(replace(replace(to_char(vl_campo_p, '999,999,999,990' || casas_decimais), ',', 'X'),'.', ','), 'X', '.'),' ','')
		into STRICT	vl_campo_w
		;

		i := 0;
		for	i in 1..length(vl_campo_w) loop
			if (substr(vl_campo_w,i,1) <> ' ') then
				vl_novo_campo_w	:= vl_novo_campo_w || substr(vl_campo_w,i,1);
			end if;
		end loop;
		end;
	end if;

	ds_retorno_w	:= substr(replace(replace(vl_novo_campo_w,'.',''),'-',''),1,30);

	return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
