CREATE OR REPLACE FUNCTION sped_obter_se_trib_piscofins ( cd_tributo_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


qt_registro_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_registro_w
from	fis_regra_sped_trib
where	cd_tributo = cd_tributo_p
and	upper(ie_tipo_tributo) = upper(ie_tipo_p);

select	count(*)
into STRICT	qt_registro_w
from	fis_regra_efd
where	((cd_tributo_pis	= cd_tributo_p)
or (cd_tributo_cofins	= cd_tributo_p));

if qt_registro_w > 0 then
	return 's';
else
	return 'n';
end if;

end;
$body$
LANGUAGE PLPGSQL
