SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ss_obter_qtd_item_conta (cd_autorizacao_p text) RETURNS bigint AS $body$
DECLARE

 
qt_item_conta_w		double precision := 0;


BEGIN 
 
select sum(x.qt_item_conta) 
into STRICT	qt_item_conta_w 
from ( 
	SELECT	sum(coalesce(a.qt_procedimento,0)) qt_item_conta 
	from	procedimento_paciente a, 
		conta_paciente b 
	where	a.nr_interno_conta = b.nr_interno_conta 
	and		coalesce(a.cd_motivo_exc_conta::text, '') = '' 
	and		b.cd_autorizacao = cd_autorizacao_p 
	
union all
 
	SELECT	sum(coalesce(a.qt_material,0)) qt_item_conta 
	from	material_atend_paciente a, 
		conta_paciente b 
	where	a.nr_interno_conta = b.nr_interno_conta 
	and		coalesce(a.cd_motivo_exc_conta::text, '') = '' 
	and		b.cd_autorizacao = cd_autorizacao_p 
	) x;
	 
 
return	qt_item_conta_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
