SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION substituir_macro_medida_laudo ( nr_seq_laudo_p bigint, nr_seq_macro_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(500);
qt_decimais_w   bigint;
ds_mascara_w	varchar(100);


BEGIN

ds_mascara_w := 'FM9999999999';

select	coalesce(max(qt_decimais),0)
into STRICT	qt_decimais_w
from	medida_exame_laudo
where	nr_sequencia = nr_seq_macro_p;

if (qt_decimais_w > 0) then

	ds_mascara_w := ds_mascara_w||'.';

	while(qt_decimais_w > 0) loop

		ds_mascara_w := ds_mascara_w||'0';
		qt_decimais_w := qt_decimais_w - 1;

	end loop;

end if;

select	max(CASE WHEN ie_tipo_valor='D' THEN ds_valor_medida WHEN ie_tipo_valor='S' THEN ds_valor_medida WHEN ie_tipo_valor='SN' THEN ds_valor_medida WHEN ie_tipo_valor='DL' THEN ds_valor_medida WHEN ie_tipo_valor='CH' THEN ds_valor_medida  ELSE CASE WHEN coalesce(QT_DECIMAIS::text, '') = '' THEN to_char(qt_medida)  ELSE REPLACE(TO_CHAR(qt_medida,ds_mascara_w),'.',',') END  END )
into STRICT	ds_retorno_w
from	medida_exame_laudo b,
	laudo_paciente_medida a
where	a.nr_seq_laudo = nr_seq_laudo_p
and	a.nr_seq_medida = b.nr_sequencia
and	b.nr_sequencia = nr_seq_macro_p;


IF (SUBSTR(ds_retorno_w,1,1) = ',') THEN
	ds_retorno_w := '0' || ds_retorno_w;
ELSIF (SUBSTR(ds_retorno_w,1,2) = '-,') THEN
	ds_retorno_w:= '-0' || REPLACE(ds_retorno_w,'-','');
END IF;


RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
