SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION substituir_macro_senha (ds_texto_p text, nr_seq_computador_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(400) :='';

ds_tempo_medio_w		varchar(255) := '';
ds_tempo_medio_local_w	varchar(255) := '';


BEGIN
ds_retorno_w := ds_texto_p;
if (position('@TempoMedioEsperaDia' in ds_retorno_w) > 0) then

	select 	substr(obter_horas_minutos(avg(OBTER_DIF_SEGUNDOS(dt_geracao_senha,dt_primeira_chamada))),1,8)
	into STRICT	ds_tempo_medio_w
	from	PACIENTE_SENHA_FILA
	where	dt_geracao_senha between trunc(clock_timestamp()) and trunc(clock_timestamp())+86399/86400
	and	(dt_primeira_chamada IS NOT NULL AND dt_primeira_chamada::text <> '');
	ds_retorno_w	:=	substr(replace(ds_retorno_w,'@TempoMedioEsperaDia',ds_tempo_medio_w),1,400);

end if;

if (coalesce(nr_seq_computador_p,0) > 0) and (position('@TempoMedioEsperaLocal' in ds_retorno_w) > 0) then

	select	obter_media_espera_local(nr_seq_computador_p)
	into STRICT	ds_tempo_medio_local_w
	;
	ds_retorno_w	:=	substr(replace(ds_retorno_w,'@TempoMedioEsperaLocal',ds_tempo_medio_local_w),1,400);
end if;

ds_retorno_w	:=	substr(replace(ds_retorno_w,'@DataHoraAtual',to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi')),1,400);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
