SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION subst_macro_integra_laudo ( ds_texto_p text, nr_atendimento_p bigint, nr_seq_laudo_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
ds_texto_w		varchar(255);
ds_texto_alterado_w	varchar(255);
pos_macro_w		bigint;
ds_macro_w		varchar(30);
nm_atributo_w		varchar(50);
pos_fim_macro_w		bigint;
vl_atributo_w		varchar(255);
ds_resultado_macro_w	varchar(255);
cd_pessoa_fisica_w	varchar(14);

 

BEGIN 
ds_texto_w		:= ds_texto_p;
ds_texto_alterado_w	:= ds_texto_w;
select	cd_pessoa_fisica 
into STRICT	cd_pessoa_fisica_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
 
if (ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') then 
	begin 
	WHILE(ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') LOOP 
		begin 
	 
		ds_texto_w	:= replace(replace(ds_texto_w,'\',' '),chr(13) || chr(10),' ');
		 
		pos_macro_w	:= position('@' in ds_texto_w);
		 
		if (pos_macro_w > 0) then 
			ds_texto_w	:= substr(ds_texto_w,pos_macro_w,length(ds_texto_w));
			pos_fim_macro_w	:= position(' ' in ds_texto_w);
			ds_macro_w	:= Elimina_Caracteres_Especiais(substr(ds_texto_w,1,pos_fim_macro_w -1));
			if ((coalesce(ds_macro_w,null) IS NOT NULL AND (coalesce(ds_macro_w,null))::text <> '')) then 
				begin 
				select	nm_atributo 
				into STRICT	nm_atributo_w 
				from	macro_prontuario 
				where	upper(nm_macro) = upper(ds_macro_w);
				exception 
				when others then 
					nm_atributo_w := null;
				end;
				 
				if ((coalesce(nm_atributo_w,null) IS NOT NULL AND (coalesce(nm_atributo_w,null))::text <> '')) then 
					begin 
					if (upper(nm_atributo_w) = 'CD_PESSOA_FISICA') then 
						vl_atributo_w	:= cd_pessoa_fisica_w;
					elsif (upper(nm_atributo_w) = 'NR_ATENDIMENTO') then 
						vl_atributo_w	:= to_char(nr_atendimento_p);
					elsif (upper(nm_atributo_w) = 'CD_PESSOA_USUARIO') then 
						select	Obter_Pessoa_Fisica_Usuario(nm_usuario_p, 'C') 
						into STRICT	vl_atributo_w 
						;
					elsif (upper(nm_atributo_w) = 'NR_LAUDO') then 
						vl_atributo_w	:= nr_seq_laudo_p;
					end if;
					ds_resultado_macro_w	:= Substituir_Macro_Texto_Tasy(upper(ds_macro_w), nm_atributo_w, vl_atributo_w);
					 
					ds_texto_alterado_w	:= replace(ds_texto_alterado_w, ds_macro_w, ds_resultado_macro_w);
					end;
				end if;
			end if;
			ds_texto_w	:= substr(ds_texto_w,pos_fim_macro_w, length(ds_texto_w));
		else 
			ds_texto_w := '';
		end if;
		 
		end;
	END loop;
	end;
end if;
 
return	ds_texto_alterado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
