	and	((DS_DOENCA IS NOT NULL AND DS_DOENCA::text <> '') or (NR_SEQ_DOENCA IS NOT NULL AND NR_SEQ_DOENCA::text <> '') or (CD_DOENCA IS NOT NULL AND CD_DOENCA::text <> ''));

c06 CURSOR FOR
