			substr(wheb_mensagem_pck.get_texto(307298) || ': ' || coalesce(qt_tentativas,0),1,50) -- Tentativas
	from   	atend_pac_dispositivo
	where  	nr_cirurgia = vl_atributo_p;

c07 CURSOR FOR
