SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION subst_ponto_virgula_adic_zero (ds_campo_p text) RETURNS varchar AS $body$
DECLARE

/*substitui ponto por virgula e adiciona 0 no início, caso o ponto esteja na primeira posição */

ds_retorno_w		varchar(2000);


BEGIN

if (philips_param_pck.get_nr_seq_idioma = 2) then -- Espanhol  - México(MX)
		ds_retorno_w := ds_campo_p;
else
		ds_retorno_w := replace(ds_campo_p,'.',',');
end if;

if (position(',' in ds_retorno_w) = 1) then
	ds_retorno_w := '0'||ds_retorno_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
