CREATE OR REPLACE FUNCTION subst_virgula_ponto_adic_zero (ds_campo_p text) RETURNS varchar AS $body$
DECLARE

/*substitui virgula por ponto e adiciona 0 no início, caso o ponto esteja na primeira posição - geralmente utilizada em laboratório*/

ds_retorno_w		varchar(2000);


BEGIN

ds_retorno_w := replace(ds_campo_p,',','.');

if (position('.' in ds_retorno_w) = 1) then
	ds_retorno_w := '0'||ds_retorno_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
