CREATE OR REPLACE FUNCTION suep_algorithms_severit_saps ( qt_saps_p bigint) RETURNS varchar AS $body$
DECLARE


ds_return_w		varchar(50);


BEGIN

if (qt_saps_p IS NOT NULL AND qt_saps_p::text <> '')	then

	CASE
		WHEN  qt_saps_p < 55 THEN ds_return_w := 'thirdCell:#00bd5e';
		WHEN  qt_saps_p >= 55 AND qt_saps_p <= 107 THEN ds_return_w := 'thirdCell:#ffcd05';
