CREATE OR REPLACE FUNCTION suep_severidade_saps ( nr_pontuacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_return_w		varchar(50);


BEGIN

		IF nr_pontuacao_p > 107 THEN ds_return_w := 'thirdCell:#ff4f4f';
		ELSIF	nr_pontuacao_p >= 55 AND nr_pontuacao_p <= 107 THEN ds_return_w := 'thirdCell:#ffcd05';
    ELSIF nr_pontuacao_p < 55 THEN ds_return_w := 'thirdCell:#25a825';
		ELSE  ds_return_w := null;
		END IF;

return ds_return_w;

end;
