CREATE OR REPLACE FUNCTION sug_obter_destinatario_email ( nr_seq_sugestao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_email_ww			varchar(2000);
ds_email_w			varchar(255);
cd_pessoa_destino_w		varchar(10);

C01 CURSOR FOR
SELECT	cd_pessoa_destino
from	sug_sugestao_dest_adic a
where	nr_seq_sugestao = nr_seq_sugestao_p
order by 1;


BEGIN

open C01;
loop
fetch C01 into
	cd_pessoa_destino_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select  max(a.ds_email)
	into STRICT	ds_email_w
	from	usuario a
	where	a.cd_pessoa_fisica = cd_pessoa_destino_w;

	ds_email_ww := ds_email_ww || ', ' || ds_email_w;
	end;
end loop;
close C01;

return ds_email_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
