CREATE OR REPLACE FUNCTION sup_dados_padrao_estoque_local ( cd_material_p bigint, cd_local_estoque_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
L - Localizacao
*/
ds_localizacao_w			varchar(80);


BEGIN

select	max(ds_localizacao)
into STRICT	ds_localizacao_w
from 	padrao_estoque_local b,
	material a
where	a.cd_material_estoque 	= b.cd_material
and	a.cd_material		= cd_material_p
and	b.cd_local_estoque	= cd_local_estoque_p;

if (ie_opcao_p = 'L') then
	return ds_localizacao_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
