CREATE OR REPLACE FUNCTION sup_obter_adto_nf_devol ( nr_seq_nf_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_adiantamento_w	bigint;

c01 CURSOR FOR
SELECT	nr_adiantamento
from	adiantamento_pago
where	nr_seq_nf = nr_seq_nf_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_adiantamento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_retorno_w	:= substr((ds_retorno_w || ', ' || nr_adiantamento_w),1,255);
	end;
end loop;
close c01;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno_w	:= substr(ds_retorno_w,3,255);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
