CREATE OR REPLACE FUNCTION sup_obter_dados_planej_compras ( nr_seq_planejamento_p bigint, ie_tipo_dado_p text) RETURNS bigint AS $body$
DECLARE

 
nr_solic_compra_w		bigint;
ds_retorno_w		varchar(255);


BEGIN 
 
select	coalesce(max(nr_solic_compra),0) 
into STRICT	nr_solic_compra_w 
from	solic_compra 
where	nr_seq_planejamento	= nr_seq_planejamento_p;
 
if (ie_tipo_dado_p = 'SC') then 
	ds_retorno_w	:= nr_solic_compra_w;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
