CREATE OR REPLACE FUNCTION sup_obter_detalhe_tec_cotacao ( nr_cot_compra_p bigint, nr_item_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w		bigint;
ds_detalhe_tecnico_w	varchar(4000) := ' ';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	cot_compra_item_detalhe
where	nr_cot_compra = nr_cot_compra_p
and	nr_item_cot_compra = nr_item_p;

if (qt_existe_w > 0) then
	select	substr(ds_detalhe,1,4000)
	into STRICT	ds_detalhe_tecnico_w
	from	cot_compra_item_detalhe
	where	nr_cot_compra = nr_cot_compra_p
	and	nr_item_cot_compra = nr_item_p;
end if;

return	ds_detalhe_tecnico_w;

end;
$body$
LANGUAGE PLPGSQL
