CREATE OR REPLACE FUNCTION sup_obter_local_valido ( cd_estabelecimento_p bigint, cd_local_estoque_p bigint, cd_material_p bigint, ie_origem_documento_p text) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w		integer;
cd_subgrupo_material_w		integer;
cd_classe_material_w		integer;
ie_local_valido_w			varchar(1) := 'N';
ie_origem_doc_w			varchar(15);

c01 CURSOR FOR
	SELECT	coalesce(ie_liberar,'S')
	from	loc_estoque_estrut_materiais
	where 	cd_local_estoque				= cd_local_estoque_p
	and	coalesce(cd_material,cd_material_p)			= cd_material_p
	and 	coalesce(cd_classe_material,cd_classe_material_w)	= cd_classe_material_w
	and 	coalesce(cd_subgrupo_material,cd_subgrupo_material_w)= cd_subgrupo_material_w
	and 	coalesce(cd_grupo_material,cd_grupo_material_w)	= cd_grupo_material_w
	order by coalesce(cd_material,0),
		coalesce(cd_classe_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_grupo_material,0);


BEGIN
/*
Alterado por Fabio em 29/04/2010 - Para tratar buscando pela procedure que faz a mesma coisa
select	nvl(max(a.cd_classe_material),0),
	nvl(max(b.cd_subgrupo_material),0),
	nvl(max(c.cd_grupo_material),0)
into	cd_classe_material_w,
	cd_subgrupo_material_w,
	cd_grupo_material_w
from	material a,
	classe_material b,
	subgrupo_material c
where	a.cd_material 		= cd_material_p
and	a.cd_classe_material 	= b.cd_classe_material
and	b.cd_subgrupo_material 	= c.cd_subgrupo_material;


open c01;
loop
fetch c01 into
	ie_local_valido_w;
exit when c01%notfound;
	ie_local_valido_w	:= ie_local_valido_w;
end loop;
close c01;*/
ie_local_valido_w := OBTER_LOCAL_VALIDO(
		cd_estabelecimento_p, cd_local_estoque_p, cd_material_p, ie_origem_documento_p, ie_local_valido_w);


return ie_local_valido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
