CREATE OR REPLACE FUNCTION sup_obter_media_compra_aprov ( cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_grupo_material_w		smallint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;
vl_retorno_w			double precision;


c01 CURSOR FOR
SELECT	a.vl_media_compra
from	sup_media_compra_aprov a,
	sup_media_compra_aprov_es b
where	a.nr_sequencia = b.nr_seq_regra
and	a.cd_estabelecimento = cd_estabelecimento_p
and	coalesce(b.cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(b.cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(b.cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and	a.dt_inicio_vigencia = 	(select	max(x.dt_inicio_vigencia)
				from	sup_media_compra_aprov x,
					sup_media_compra_aprov_es y
				where	x.nr_sequencia = y.nr_seq_regra
				and	x.cd_estabelecimento = cd_estabelecimento_p
				and	coalesce(y.cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
				and	coalesce(y.cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
				and	coalesce(y.cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
				and	trunc(x.dt_inicio_vigencia) <= trunc(clock_timestamp()))
order by
	coalesce(b.cd_classe_material, 0),
	coalesce(b.cd_subgrupo_material, 0),
	coalesce(b.cd_grupo_material, 0);


BEGIN

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	vl_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	vl_retorno_w := vl_retorno_w;
	end;
end loop;
close C01;


return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
