CREATE OR REPLACE FUNCTION sup_obter_metodo_valorizacao (dt_mesano_referencia_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


dt_mes_inicio_vigencia_w	timestamp;
ie_metodo_valorizacao_w	varchar(15);
qt_existe_w		bigint;


BEGIN
select	count(*)
into STRICT	qt_existe_w
from	regra_valorizacao_estoque
where	cd_estabelecimento = cd_estabelecimento_p;

if (qt_existe_w > 0) then
	begin
	select	max(dt_mes_inicio_vigencia)
	into STRICT	dt_mes_inicio_vigencia_w
	from	regra_valorizacao_estoque
	where	dt_mes_inicio_vigencia <= dt_mesano_referencia_p
	and	cd_estabelecimento = cd_estabelecimento_p;

	select	coalesce(max(ie_metodo_valorizacao),'CMM')
	into STRICT	ie_metodo_valorizacao_w
	from	regra_valorizacao_estoque
	where	dt_mes_inicio_vigencia = dt_mes_inicio_vigencia_w
	and	cd_estabelecimento = cd_estabelecimento_p;
	end;
else
	begin
	select	coalesce(max(ie_metodo_valorizacao),'CMM')
	into STRICT	ie_metodo_valorizacao_w
	from	parametro_estoque
	where	ie_situacao = 'A'
	and	cd_estabelecimento = cd_estabelecimento_p;
	end;
end if;

return	ie_metodo_valorizacao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
