CREATE OR REPLACE FUNCTION sup_obter_nome_comprador ( cd_estabelecimento_p bigint, cd_comprador_p text) RETURNS varchar AS $body$
DECLARE


nm_guerra_w			comprador.nm_guerra%type;
cd_estabelecimento_w		integer	:= wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (cd_estabelecimento_p <> 0) then
	cd_estabelecimento_w	:= cd_estabelecimento_p;
end if;

if (cd_estabelecimento_w IS NOT NULL AND cd_estabelecimento_w::text <> '') and (cd_comprador_p IS NOT NULL AND cd_comprador_p::text <> '') then
	select	coalesce(max(nm_guerra),'')
	into STRICT	nm_guerra_w
	from	comprador
	where	cd_estabelecimento	= cd_estabelecimento_w
	and	cd_pessoa_fisica	= cd_comprador_p;
end if;

return nm_guerra_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
