SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sup_obter_operacao_unit (ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*ie_opcao_w
et - entrada transferência
st - saída transferência
ep - produção
sp - consumo produção
etd - entrada transferência devolução
std - saída transferência devolução
*/
cd_operacao_estoque_w	smallint;

BEGIN
if (ie_opcao_p = 'ST') then
	begin
	select	min(cd_operacao_estoque)
	into STRICT	cd_operacao_estoque_w
	from	operacao_estoque
	where	ie_situacao = 'A'
	and	coalesce(ie_consignado,'0') = '0'
	and	ie_tipo_requisicao = '21'
	and	ie_entrada_saida = 'S'
	and	ie_atualiza_estoque = 'S';
	end;
elsif (ie_opcao_p = 'ET') then
	begin
	/*primeito busca a operação de saída de transferência*/

	select	min(cd_operacao_estoque)
	into STRICT	cd_operacao_estoque_w
	from	operacao_estoque
	where	ie_situacao = 'A'
	and	coalesce(ie_consignado,'0') = '0'
	and	ie_tipo_requisicao = '21'
	and	ie_entrada_saida = 'S'
	and	ie_atualiza_estoque = 'S';

	/*para então buscar a operação correspondente*/

	select	cd_operacao_correspondente
	into STRICT	cd_operacao_estoque_w
	from	operacao_estoque
	where	cd_operacao_estoque = cd_operacao_estoque_w;

	if (coalesce(cd_operacao_estoque_w::text, '') = '') then
		select	min(cd_operacao_estoque)
		into STRICT	cd_operacao_estoque_w
		from	operacao_estoque
		where	ie_situacao = 'A'
		and	coalesce(ie_consignado,'0') = '0'
		and	ie_tipo_requisicao = '21'
		and	ie_entrada_saida = 'E'
		and	ie_atualiza_estoque = 'S';
	end if;
	end;
elsif (ie_opcao_p = 'STD') then
	begin
	select	min(cd_operacao_estoque)
	into STRICT	cd_operacao_estoque_w
	from	operacao_estoque
	where	ie_situacao = 'A'
	and	coalesce(ie_consignado,'0') = '0'
	and	ie_tipo_requisicao = '2'
	and	ie_entrada_saida = 'S'
	and	ie_atualiza_estoque = 'S';
	end;
elsif (ie_opcao_p = 'ETD') then
	begin
	/*primeito busca a operação de saída de transferência*/

	select	min(cd_operacao_estoque)
	into STRICT	cd_operacao_estoque_w
	from	operacao_estoque
	where	ie_situacao = 'A'
	and	coalesce(ie_consignado,'0') = '0'
	and	ie_tipo_requisicao = '2'
	and	ie_entrada_saida = 'S'
	and	ie_atualiza_estoque = 'S';

	/*para então buscar a operação correspondente*/

	select	cd_operacao_correspondente
	into STRICT	cd_operacao_estoque_w
	from	operacao_estoque
	where	cd_operacao_estoque = cd_operacao_estoque_w;

	if (coalesce(cd_operacao_estoque_w::text, '') = '') then
		select	min(cd_operacao_estoque)
		into STRICT	cd_operacao_estoque_w
		from	operacao_estoque
		where	ie_situacao = 'A'
		and	coalesce(ie_consignado,'0') = '0'
		and	ie_tipo_requisicao = '2'
		and	ie_entrada_saida = 'E'
		and	ie_atualiza_estoque = 'S';
	end if;
	end;
end if;

if (coalesce(cd_operacao_estoque_w,0) = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(192653);
end if;

return	cd_operacao_estoque_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
