CREATE OR REPLACE FUNCTION sup_obter_permite_unitarizar ( cd_estabelecimento_p bigint, nr_seq_lote_fornec_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_permissao_w		varchar(1) := 'S';
ds_retorno_w		varchar(1);
qt_regra_w		integer;

ie_origem_lote_w		varchar(15);
cd_material_w		integer;
cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;

c01 CURSOR FOR
SELECT	ie_permissao
from	regra_permissao_unitarizar
where	coalesce(cd_grupo_material, cd_grupo_material_w) = cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w) = cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w) = cd_classe_material_w
and	coalesce(cd_material, cd_material_w) = cd_material_w
and	coalesce(ie_origem_lote, ie_origem_lote_w) = ie_origem_lote_w
order by	ie_origem_lote desc,
	cd_material desc,
	cd_classe_material desc,
	cd_subgrupo_material desc,
	cd_grupo_material desc;


BEGIN
select	count(*)
into STRICT	qt_regra_w
from	regra_permissao_unitarizar
where	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p;

if (qt_regra_w > 0) then
	begin
	ie_permissao_w := 'N';
	begin
	select	cd_material,
		coalesce(ie_origem_lote,'0')
	into STRICT	cd_material_w,
		ie_origem_lote_w
	from	material_lote_fornec
	where	nr_sequencia = nr_seq_lote_fornec_p;
	exception
	when others then
		cd_material_w := 0;
	end;

	if (cd_material_w > 0) then
		begin
		select	cd_grupo_material,
			cd_subgrupo_material,
			cd_classe_material
		into STRICT	cd_grupo_material_w,
			cd_subgrupo_material_w,
			cd_classe_material_w
		from	estrutura_material_v
		where	cd_material = cd_material_w;

		open c01;
		loop
		fetch c01 into
			ie_permissao_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		end loop;
		close c01;
		end;
	else
		ie_permissao_w := 'N';
	end if;
	end;
end if;

ds_retorno_w	:= coalesce(ie_permissao_w,'N');

return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
